/* CLASS TO JS ANIMATE*/

.moveright {
  animation: moveright 0.7s 0.1s ease forwards;
}

.moveleft {
  animation: moveleft 0.9s 0.1s ease forwards;
}

.moveleftWelcome {
  animation: moveleftWelcome 0.7s 0.1s ease forwards;
}

.moverightWelcome {
  animation: moverightWelcome 0.9s 0.2s ease forwards;
}

