#filter-screen {
  position: fixed;
  height: 100vh;
  background-color: #689793;
  z-index: 15;
  top: 0;
  left: 0;
  transition: all ease 1s;
  overflow-x: hidden;
}

#filter-wrapper {
  height: 100%;
  width: 100%;
  transition: all ease 1s;
}

.cat-link-wrapper {
  width: 85%;
  height: 30%;
  margin-left: 15%;
  margin-top: 15%;
}

.cat-link {
  /* H1 */
  width: 100%;
  height: 20%;
}

.cat-link h1 {
  margin: 0;
  height: 100%;
  padding: 1rem 0 1rem 0;
}

.cat-link,
.cat-project-link {
  color: white;
  display: block;
  padding-left: 2rem;
}

