/* ABOUT SCREEN */

/* SLIDING */

.slide-anim {
  visibility: hidden;
  visibility: visible; /*For old IE browsers IE6-8 */
}
.slide-anim .slide {
  visibility: visible;
  animation: slide 1s;
}
.slide-anim::after {
  /* useful when its child elements are float:left; */
  content: "";
  display: table;
  clear: both;
}
@keyframes slide {
  0% {
    opacity: 0;
    transform: translateY(50%);
  }
  100% {
    opacity: 1;
    transform: translateY(0);
  }
}

